import React, { Component } from 'react'
import { Form } from 'semantic-ui-react'

const options = [
  { key: 'm', text: 'Male', value: 'male' },
  { key: 'f', text: 'Female', value: 'female' },
]

class SignUp extends Component {
  state = {
    name: "",
    password:""
  }

createUser =()=> {

  fetch('http://localhost:3000/users', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      Accept: 'application/json'
    },
    body: JSON.stringify({
      user: {
        username: 'guy',
        password: 'hi',
        avatar: 'https://upload.wikimedia.org/wikipedia/commons/9/9a/Guy_Fieri_at_Guantanamo_2.jpg'
      }
    })
  })
  .then(r => r.json())
  .then(console.log)

}


  handleChange = (e) => this.setState({
    [e.target.name]: e.target.value
    })

  render() {
    console.log(this.state)
    const { value } = this.state
    return (
      <Form onSubmit={this.createUser}>
        <Form.Group widths='equal'>
          <Form.Input
          fluid label='First name' placeholder='First name'
          name="name"
          value={this.state.name}
          onChange={(e) => this.handleChange(e)}/>
          <Form.Input
          fluid label='Password' placeholder='Last name'
          name="password"
          type="password"
          value={this.state.password}
          onChange={(e) => this.handleChange(e)}
          />
          <Form.Select fluid
           label='Gender' options={options} placeholder='Gender' />
        </Form.Group>
        <Form.Checkbox label='I agree to the Terms and Conditions' />
        <Form.Button>Submit</Form.Button>
      </Form>
    )
  }
}

export default SignUp
