import React, { Component } from 'react';
import './App.css';
import {Route,Switch} from 'react-router-dom'
import Home from './Components/Home'
import Login from './Components/Login'
import SignUp from './Components/SignUp'
import User from './Containers/User'
import NavBar from './Components/NavBar'
import NewTeam from './Containers/NewTeam'
import Locations from './Containers/Locations'

class App extends Component {

  state={
    user: null
  }

  componentDidMount(){
    console.log("test")
    fetch('http://localhost:3000/users/1')
    .then(res => res.json())
    .then(user => this.setState({ user }))
  }


  createUser =(newUser)=> {


    fetch('http://localhost:3000/users', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json'
      },
      body: JSON.stringify({
        user: {
          username: this.state.username,
          password: this.state.password,
          avatar: 'https://upload.wikimedia.org/wikipedia/commons/9/9a/Guy_Fieri_at_Guantanamo_2.jpg'
        }
      })
    })
    .then(r => r.json())
    .then(console.log)

  }

  handleSignUpSubmit =(e,newUser)=> {
    e.preventDefault()
  this.createUser(newUser)
  this.props.history.push('/drinkMain')


  }

  render() {

    return (
      <div>
      <NavBar user={this.state.user}/>
      <Switch>
      <Route exact path="/Locations" render={() => <Locations

        />}/>
      <Route exaxt path="/NewTeam" component={NewTeam}/>
      <Route exact path="/bball" component={Home}/>
      <Route exact path="/login" component={Login}/>
      <Route exact path="/signUP" render={() => <SignUp handleSignUpSubmit={this.handleSignUpSubmit}/>}/>
      <Route path ="/users/:id" render={()=>
         <User
         user={this.state.user}
        />}/>

      </Switch>
      </div>
    );
  }
}

export default App;
