import React, { Component } from 'react';
import './App.css';
import {Route,Switch} from 'react-router-dom'
import Home from './Components/Home'
import Login from './Components/Login'
import SignUp from './Components/SignUp'
import User from './Containers/User'
import NavBar from './Components/NavBar'
import NewTeam from './Containers/NewTeam'
import Locations from './Containers/Locations'

class App extends Component {

  state={
    user: null
  }

  // componentDidMount(){
  //   console.log("test")
  //   fetch('http://localhost:3000/users/1')
  //   .then(res => res.json())
  //   .then(user => this.setState({ user }))
  // }


  createUser =(newUser)=> {


    fetch('http://localhost:3000/users', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json'
      },
      body: JSON.stringify({
        user: {
          username: newUser.username,
          password: newUser.password,
          avatar: newUser.avatar
        }
      })
    })
    .then(r => r.json())
    .then(newUser => this.setState({
      user: newUser
    }))

  }

  handleSignUpSubmit =(e,newUser)=> {
    e.preventDefault()
  this.createUser(newUser)
  // this.props.history.push('/userMain')


  }

  render() {

    return (
      <div>
      <NavBar user={this.state.user}/>
      <Switch>
      <Route exact path="/Locations" render={() => <Locations

        />}/>
      <Route exaxt path="/NewTeam" component={NewTeam}/>
      <Route exact path="/bball" component={Home}/>
      <Route exact path="/login" component={Login}/>
      <Route exact path="/signUP" render={() => <SignUp handleSignUpSubmit={this.handleSignUpSubmit}/>}/>
      <Route path ="/userMain" render={()=>
         <User
         user={this.state.user}
        />}/>

      </Switch>
      </div>
    );
  }
}

export default App;
