    return (
      <React.Fragment>

      <Grid columns={2} padded>
      <Grid.Column>
        <div className="places">
        <Places venues={this.state.venues}/>
        </div>
      </Grid.Column>
      <Grid.Column>
      <div className="maps map">
      <CurrentLocation
          centerAroundCurrentLocation
          google={this.props.google}
        >
      {this.state.venues && this.state.venues.length > 0 ?

          <><Markers/></>

