          name={'chicken'}
          position={{lat: 40.75768883549578, lng: -73.98883631344103}}/>
          <InfoWindow
            marker={this.state.activeMarker}
            visible={this.state.showingInfoWindow}
            onClose={this.onClose}
          >
            <div>
              <h4>{this.state.selectedPlace.name}</h4>
            </div>
          </InfoWindow>
        </CurrentLocation>
        </div>

      </Grid.Column>
    </Grid>



















        </React.Fragment>


    );
  }
}

