    //   })
    //   console.log(venueMarkers[0]);
    //   return venueMarkers
    // }
    markers = () => {
      console.log("STATE", this.state.venues)
      //
      return this.state.venues.map(venue => {
        return <Marker
            key={venue.id}
            onClick={this.onMarkerClick}
            title={venue.name}
            name={venue.name}
            position={{lat: venue.location.lat, lng: venue.location.lng}}
            />
      })

      // return <Marker
