import React, { Component } from 'react';
import Places from '../Components/Places'
import CurrentLocation from '../Components/CurrentLocation'
import superagent from 'superagent'
import {Map,InfoWindow,Marker,GoogleApiWrapper} from 'google-maps-react'



  export class Location extends Component {

    state = {
      showingInfoWindow: false,  //Hides or the shows the infoWindow
      activeMarker: {},          //Shows the active marker upon click
      selectedPlace: {},         //Shows the infoWindow to the selected place upon a marker
      venues: [],
      markerObjects: []

    };

    componentDidMount(){

      const url = 'https://api.foursquare.com/v2/venues/search?&ll=40.7575285,-73.9884469&client_id=IULHTNQOQMGV0YJS1I0R35HEIDUZUL51HK4P4AYMBX3R1ORV&client_secret=M3Q44WIHHYS3MCJAHHXDLGHDLFILHPZENP2LE5R5MS2YTWKD&v=20181212'

      superagent
      .get(url)
      .query(null)
      .set('Accept','text/json')
      .end((error, response) => {

        const venues = response.body.response.venues

        console.log(JSON.stringify(venues))
        this.setState({venues})
      })
    }

    onMarkerMounted = element => {
      this.setState(prevState => ({
        markerObjects: [...prevState.markerObjects, element.marker]
      }))
    };


    onMarkerClick = (props, marker, e) => {
      this.setState({
        selectedPlace: props,
        activeMarker: marker,
        showingInfoWindow: true
      })
    }

    onClose = props => {
      if(this.state.showingInfoWindow) {
        this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }

    }


  render() {
    const style ={
      width: '60%',
      height: '60%'
    }

    let venueMarkers = this.state.venues.map(venue => <Marker
    key={venue.id}
    onClick={this.onMarkerClick}
    title={venue.name}
    name={venue.name}
    position={{lat: venue.lat, lng: venue.lng}} />)

    return (
      <React.Fragment>
      < div>
      <Places venues={this.state.venues}/>
      </div>

      <div style={style}>
      <CurrentLocation
          centerAroundCurrentLocation
          google={this.props.google}
        >
        {venueMarkers}
          <Marker
          ref={this.onMarkerMounted}
          onClick={this.onMarkerClick}
          name={'current location'} />
          <InfoWindow
            marker={this.state.activeMarker}
            visible={this.state.showingInfoWindow}
            onClose={this.onClose}
          >
          <Marker markers={venueMarkers}/>
            <div>
              <h4>{this.state.selectedPlace.name}</h4>
            </div>
          </InfoWindow>
        </CurrentLocation>
        </div>

        </React.Fragment>


    );
  }
}

export default GoogleApiWrapper({
  apiKey: ("AIzaSyDlbBSYoX6bM9k_ZmDCMopQhHPiPI5wjR8")
})(Location)






